def jinti(p,a,ax,ay,aa,area,n):
    an = np.array([0,0,0,0])

    if aa[ax][ay-1] == -1:
        aa[ax][ay] = -1
    elif aa[ax+1][ay] == -1:
        aa[ax][ay] = -1
    elif aa[ax][ay+1] == -1:
        aa[ax][ay] = -1
    elif aa[ax-1][ay] == -1:
        aa[ax][ay] = -1
    
    if aa[ax][ay-1]!=10 and aa[ax][ay-1]!=-1:
        an[0] = area[0][ax][ay-1]
    if aa[ax+1][ay]!=10 and aa[ax+1][ay]!=-1:
        an[1] = area[0][ax+1][ay]
    if aa[ax][ay+1]!=10 and aa[ax][ay+1]!=-1:
        an[2] = area[0][ax][ay+1]
    if aa[ax-1][ay]!=10 and aa[ax-1][ay]!=-1:
        an[3] = area[0][ax-1][ay]

    if aa[ax][ay] == -1:
        for i in an:
            if i != 0:
                aa -= area[i]//i

    else:
        if np.all(an == np.array([0,0,0,0])):
            for i in range(n-np.shape(area)[0]+1):
                area = np.append(area,[np.zeros_like(aa)], axis=0)
            area[n] = np.zeros_like(aa)
            area[n][ax][ay] = n
            area[0][ax][ay] = n
            n += 1
            if n == 10:n += 1
            
        
        else:
            an = np.sort(an)
            area[an[3]][ax][ay] = an[3]
            for i in range(3):
                if an[i] != 0:
                    if an[i] != an[i+1]:
                        area[an[i]] += area[an[i+1]]//an[i+1]*an[i]
                        area[an[i+1]] = np.zeros_like(aa)

    for i in range(np.shape(aa)[0]-ay-1):
        for j in range(np.shape(aa)[1]-ax-2):
            if aa[ax+j+1][ay+i] == 0:
                jinti(p,a,ax+j+1,ay+i,aa,area,n)
                # print(np.array(aa))
                # return 0
                
        ax=0
    


        
